var root = new java.util.HashMap();
var orders = new java.util.ArrayList();

var sdf = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss");

var orderIterator = ICT.MAP.ITERATOR('OrdersMap');
while (orderIterator.hasNext()) {
    var order = orderIterator.next();
    var shipments = ICT.MAP.LIST(order.shipments.shipment);
    var payments = ICT.MAP.LIST(order.payments.payment); 
    var record = {
        OrderDate: order['order-date'],
        OrderNumber: order['order-no'],
        OrderContact: order.customer['customer-name'],
        OrderReferencePONo: order['invoice-no'],
        FirstName: order.customer['billing-address']['first-name'],
        LastName: order.customer['billing-address']['last-name'],
        OrderAmount: order.totals['order-total']['gross-price'],
        OrderAmountShipping: order.totals['shipping-total']['gross-price'],
        OrderAmountTax: order.totals['order-total'].tax,
        OrderShippingAddress1: shipments[0]['shipping-address'].address1,
        OrderShippingAddress2: shipments[0]['shipping-address'].address2,
        OrderShippingCity: shipments[0]['shipping-address'].city,
        OrderShippingStateProvince: shipments[0]['shipping-address']['state-code'],
        OrderShippingZip: shipments[0]['shipping-address']['postal-code'],
        OrderShippingPhone: shipments[0]['shipping-address'].phone,
        OrderShippingCountry: shipments[0]['shipping-address']['country-code'],
        OrderShippingCountryCode: shipments[0]['shipping-address']['country-code'],
        OrderCardName: payments[0]['credit-card']['card-holder'],
        OrderCardType: payments[0]['credit-card']['card-type'],  
        OrderComeFrom: order['created-by'],
        WeatherInfo: weatherAPI.byPostalCode(shipments[0]['shipping-address']['postal-code']),

        OrderAuths: [],
        OrderItems: []
    };

    // add our items
    var orderItemIter = ICT.MAP.LIST(order["product-lineitems"]["product-lineitem"]).iterator();
    while (orderItemIter.hasNext()) {
        var thisItem = orderItemIter.next();
        record.OrderItems[record.OrderItems.length] = {
            ItemProduct: {
                Price: {
                    PriceSale: thisItem['net-price']
                },
                ProductPartNo: thisItem['product-id'],
                ProductCode: thisItem['product-name']
            },
            OrderNumber: order['order-no'],
            ItemPrice: thisItem['net-price'],
            ItemQuantity: thisItem.quantity,
            ItemTax: thisItem.tax,
            ItemTotal: thisItem['gross-price']
        };
    }
    orders.add(record);
}
root.put("Orders", orders);
dict.put("Root", root);

